const express = require('express');
const QRCode = require('qrcode');
const app = express();
const port = 3000;

// Serve static files from the "public" folder
app.use(express.static('public'));

// Middleware to parse form data
app.use(express.urlencoded({ extended: true }));

// POST route to generate QR Code
app.post('/generate', async (req, res) => {
  const { text } = req.body;
  try {
    const qrDataURL = await QRCode.toDataURL(text);
    res.send(`
      <h2>QR Code for: ${text}</h2>
      <img src="${qrDataURL}" alt="QR Code" />
      <br/>
      <a href="${qrDataURL}" download="qrcode.png">Download QR Code</a>
      <br/><br/>
      <a href="/">Generate Another</a>
    `);
  } catch (err) {
    res.status(500).send('Failed to generate QR code');
  }
});

// Start the server
app.listen(port, () => {
  console.log(`QR Code Generator running at http://localhost:${port}`);
});

const express = require("express");
const path = require("path");
const cookieParser = require("cookie-parser");
const pasteRoutes = require("./routes/pasteRoutes");

const app = express();
const PORT = 3300;

app.use(express.urlencoded({ extended: false }));
app.use(cookieParser());
app.use(express.static(path.join(__dirname, "public")));

app.set("view engine", "ejs");
app.set("views", path.join(__dirname, "views"));

app.use("/", pasteRoutes);

app.listen(PORT, () => console.log(`Pastebin running at http://localhost:${PORT}`));
